import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate= useNavigate();
    const[user,setUser]=useState({});
    const inputHandler=(e)=>{
        setUser({
            ...user,[e.target.name]:e.target.value
        })
       console.log(user);
    }
    const addHandler=()=>{
        console.log("button clicked",user);
        axios.post("http://localhost:3000/api/login",user)
        .then((response)=>{
            console.log(response);
            console.log(response.data.message);
            if(response.data.message ==="Login sucessfully"){
                const token=response.data.token;
                const userid=response.data.data._id;
                const userStatus=response.data.data.userStatus;
                console.log(response);
                console.log(token);
                console.log(userid);
                console.log(userStatus);
                sessionStorage.setItem("userToken",token);
                sessionStorage.setItem("userId", userid);
                sessionStorage.setItem("userStatus", userStatus);
                alert(response.data.message);
                navigate('/viewemployee')
                
            }
            else{
                alert(response.data.message);

            }
        })
    } 
  return (
    <div>

        <div className="container">
            <section className="section-register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
                            <div className="d-flex justify-content-center py-4">
                                <a href="index.html" className="logo d-flex align-items-center w-auto">
                                    <img src="./assets/img/ict_logo.png" alt="" />
                                    <span className="d-none d-lg-block">ICTAK Curriculum Tracker</span>
                                </a>
                            </div>
                            <div className="card mb-3">
                                <div className="card-body">
                                    <div className="pt-4 pb-2">
                                    <h5 className="card-title text-center pb-0 fs-4">Login</h5>
                                    </div>
                                    <div className="col-12">
                                    <label htmlFor="yourUsername" className="form-label">Username</label>
                                    <div className="input-group has-validation">
                                        <span className="input-group-text" id="inputGroupPrepend">@</span>
                                        <input
                                        type="text"
                                        name="username"
                                        className="form-control"
                                        onChange={inputHandler}
                                        />
                                    </div>
                                    </div>
                                    <br />
                                    <div className="col-12">
                                    <label htmlFor="yourUsername" className="form-label">Password</label>
                                    <input
                                        type={showPassword ? 'text' : 'password'}
                                        name="password"
                                        className="form-control"
                                        onChange={inputHandler}
                                    />
                                    </div>
                                    <br />
                                    <div className="col-12">
                                    <div className="form-check">
                                        <input
                                        className="form-check-input"
                                        type="checkbox"
                                        onChange={toggleShowPassword}
                                        />
                                        <span>Show Password</span>
                                    </div>
                                    </div>
                                    <br/>
                                    <div className="col-12">
                                    <button className="btn btn-primary w-100" onClick={addHandler}>Login</button>
                                    </div>
                                    <div className="col-12">
                                    <p className="small mb-0">Don't have an account? <a href="/signup">Create an account</a></p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
      </div>

        <div className="container">
            <div className="row">
                <div className="col col-12 col-sm-12 col-md-12">
                    <h1>BLog APP -LOGIN</h1>
                    <div className="row g-3">
                        <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                            <label htmlFor="" className="form-label" >userName</label>
                            <input type="text" className="form-control" name="username" onChange={inputHandler}/>
                        </div>
                        <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                            <label htmlFor="" className="form-label">Password</label>
                            <input type="password" name="password" id="" className="form-control" onChange={inputHandler} />
                        </div>
                        
                        <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                        <button className="btn btn-success align-center" onClick={addHandler}>Login</button>
                        </div>
                        <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                            <a href="/register">New user click Here</a>
                        </div>
                    </div>
                </div>
            </div>
        </div>




    </div>
  )
}

export default Login
