import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Addpost from './Addpost'

const ViewallEmployee = () => {

    const[data,setData]=useState([]);
    const[update,setUpdate] = useState(false);
    const[singleValue,setSingleValue] = useState([]);
  
    const fetchDataFromApi = () =>{
      axios
      .get(
      
        "http://localhost:3000/api/getdata"
        ) 
       .then((response)=>{
        console.log(response.data);
        setData(response.data);
  
       })
    }
   const deleteBlog=(id)=>{
    console.log("delete clicked");
    console.log(id);
    axios.delete("http://localhost:3000/api/delete/"+id)
    .then((response)=>{
      alert(response.data.message);
      window.location.reload(false);
    })
    
   }
   const updateBlog=(val) => {
    console.log("update clicked",val);
    setUpdate(true);
    setSingleValue(val);
    
  
   }
  
    useEffect(()=>{
      fetchDataFromApi()
    },[]);
  

    let finalJSX= <div className="container">
    <div className="row">
      <div className="col col-12 col-sm-12 col-md-12">
  
        <div className="row g-3">
          {data.map((value,index)=>{
            return<div class="card mb-3">
            <div class="row g-0">
              <div class="col-md-4">
                <img src="./img/emp.png"class="img-fluid rounded-start" alt="..."/>
              </div>
              <div class="col-md-8">
                <div class="card-body">
                  <h5 class="card-title">{value.name}</h5>
                  <p class="card-text">{value.address}</p>
                  <p class="card-text">{value.phone}</p>
                  <p class="card-text">{value.email}</p>
                  <p class="card-text">{value.post}</p>
                  <p class="card-text"><small class="text-body-secondary">{value.salary}</small></p>
                  <p class="card-text">
                    <small class="text-body-secondary">
                      <button className='btn btn-danger' onClick={()=>deleteBlog(value._id)}>Delete</button>
                     </small>&nbsp;
                     <small class="text-body-secondary">
                      <button className='btn btn-primary' onClick={()=>updateBlog(value)}>update</button>
                     </small>
                     </p>
                </div>
              </div>
            </div>
          </div>
  
          })}
          
        </div>
      </div>
    </div>
  </div>
    if(update) finalJSX=<Addemployee method='put' data={singleValue} />
      return (
      
        finalJSX
  
    )
}

export default ViewallEmployee
