import React,{useState} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const Register = () => {
    const[inputs,setInputs] = useState({});
    const navigate=useNavigate();


    const inputHandler = (e)=>{
        console.log("onchange");
        
        if (Object.keys(errors).length > 0) {
            validateFields();
          }
      


        setInputs({
            ...inputs,[e.target.name]: e.target.value
        })
        console.log(inputs);
    }

    const submitHandler = ()=>{
        if (validateFields()) {


        }
       
    }
    
    const validateFields = () => {
        const { name, email, username, password, confirmPassword } = inputs;
        const newErrors = {};
    
        if (!name) {
          newErrors.name = 'Please enter your name!';
        }
        if (!email) {
          newErrors.email = 'Please enter your email!';
        } else if (!/\S+@\S+\.\S+/.test(email)) {
          newErrors.email = 'Please enter a valid email address!';
        }
        if (!username) {
          newErrors.username = 'Please enter your username!';
        }
        if (!password) {
          newErrors.password = 'Please enter your password!';
        }
        if (!confirmPassword) {
          newErrors.confirmPassword = 'Please confirm your password!';
        } else if (password !== confirmPassword) {
          newErrors.confirmPassword = 'Passwords do not match!';
        }
    
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
      };
    



  return (
    <div>

        <div className="container">
            <section className="section min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">

                            <div className="card">
                                <div className="card-body">
                                    <div className="pb-2">
                                        <h5 className="card-title text-center fs-4">Signup</h5>
                                    </div>

                                    <div className="col-12">
                                        <label htmlFor="" className="form-label">Name</label>
                                        <input type="text" className="form-control" name="name" onChange={inputHandler}/>
                                        {errors.name && <div className="invalid-feedback d-block">{errors.name}</div>}
                                    </div>
                                    <div className="col-12">
                                         <label htmlFor="" className="form-label">Email ID</label>
                                        <input type="text" name="email"  className="form-control"  onChange={inputHandler}/>
                                        {errors.email && <div className="invalid-feedback d-block">{errors.email}</div>}
                                    </div>
                                    <div className="col-12">
                                            <label htmlFor="" className="form-label">User Name</label>
                                                <input type="text" name="username"  className="form-control" onChange={inputHandler}/>
                                            {errors.username && <div className="invalid-feedback d-block">{errors.username}</div>}
                                        
                                    </div>
                                    <div className="col-12">
                                        <label htmlFor="" className="form-label">Password</label>
                                         <input type="password" name="password"  className="form-control" onChange={inputHandler}/>
                                        {errors.password && <div className="invalid-feedback d-block">{errors.password}</div>}
                                    </div>
                                    <div className="col-12">
                                        <label htmlFor="confirmPassword" className="form-label">
                                            Confirm Password
                                        </label>
                                        <input
                                            type="password"
                                            name="confirmPassword"
                                            className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                                            id="confirmPassword"
                                            onChange={inputHandler}
                                            required
                                        />
                                        {errors.confirmPassword && (
                                            <div className="invalid-feedback d-block">{errors.confirmPassword}</div>
                                        )}
                                    </div>

                                    <div className="col-12">
                                        <button className="btn btn-primary btn-block"  onClick={submitHandler}>
                                            Create Account
                                        </button>
                                    </div>
                                    <div className="col-12">
                                        <p className="mb-0">
                                            Already have an account? <a href="/">Log in</a>
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>






     <div className="container">
            <div className="row">
                <div className="col col-12 col-sm-12 col-md-12">
                    <h1>SIGNUP</h1>
                    <div className="row g-3">
                        <div className="col col-12 col-sm-6 col-md-6 col-lg-6">
                            <label htmlFor="" className="form-label">Name</label>
                            <input type="text" className="form-control" name="name" onChange={inputHandler}/>
                        </div>
                        <div className="col col-12 col-sm-6 col-md-6 col-lg-6">
                            <label htmlFor="" className="form-label">Email ID</label>
                            <input type="text" name="email"  className="form-control"  onChange={inputHandler}/>
                        </div>
                        <div className="col col-12 col-sm-6 col-md-6 col-lg-6">
                            <label htmlFor="" className="form-label">User Name</label>
                            <input type="text" name="username"  className="form-control" onChange={inputHandler}/>
                        </div>
                        <div className="col col-12 col-sm-6 col-md-6 col-lg-6">
                            <label htmlFor="" className="form-label">Password</label>
                            <input type="password" name="password"  className="form-control" onChange={inputHandler}/>
                        </div>
                        <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                          <button className="btn btn-danger align-center" onClick={submitHandler}>Register</button>
                        </div>
                        <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                            <a href="/">Back to Login</a>
                        </div>
                    </div>
                </div>
            </div>
        </div>





    </div>
  )
}

export default Register
