const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
  });
  const [errors, setErrors] = useState({});

  const inputHandler = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    // Validate the form data before submitting
    const validationErrors = {};
    if (formData.name.trim() === '') {
      validationErrors.name = 'Name is required';
    }
    if (formData.email.trim() === '') {
      validationErrors.email = 'Email is required';
    }
    if (formData.username.trim() === '') {
      validationErrors.username = 'Username is required';
    }
    if (formData.password.trim() === '') {
      validationErrors.password = 'Password is required';
    }

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Form data is valid, perform the sign-up POST request to the backend
    axios
      .post('http://localhost:3000/api/signup', formData)
      .then((response) => {
        // Handle success (e.g., show success message, redirect to login page, etc.)
        console.log('Sign-up successful:', response.data);
      })
      .catch((error) => {
        // Handle error (e.g., show error message, etc.)
        console.error('Error during sign-up:', error);
      });
  };

  return (
    <div className="container">
      <section className="section min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
              <div className="card">
                <div className="card-body">
                  <div className="pb-2">
                    <h5 className="card-title text-center fs-4">Signup</h5>
                  </div>

                  <div className="col-12">
                    <label htmlFor="" className="form-label">
                      Name
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      name="name"
                      onChange={inputHandler}
                    />
                    {errors.name && (
                      <div className="invalid-feedback d-block">{errors.name}</div>
                    )}
                  </div>
                  <div className="col-12">
                    <label htmlFor="" className="form-label">
                      Email ID
                    </label>
                    <input
                      type="text"
                      name="email"
                      className="form-control"
                      onChange={inputHandler}
                    />
                    {errors.email && (
                      <div className="invalid-feedback d-block">{errors.email}</div>
                    )}
                  </div>
                  <div className="col-12">
                    <label htmlFor="" className="form-label">
                      User Name
                    </label>
                    <input
                      type="text"
                      name="username"
                      className="form-control"
                      onChange={inputHandler}
                    />
                    {errors.username && (
                      <div className="invalid-feedback d-block">{errors.username}</div>
                    )}
                  </div>
                  <div className="col-12">
                    <label htmlFor="" className="form-label">
                      Password
                    </label>
                    <input
                      type="password"
                      name="password"
                      className="form-control"
                      onChange={inputHandler}
                    />
                    {errors.password && (
                      <div className="invalid-feedback d-block">{errors.password}</div>
                    )}
                  </div>
                  <br />
                  <div className="col-12">
                    <button className="btn btn-primary btn-block" onClick={submitHandler}>
                      Create Account
                    </button>
                  </div>
                  <div className="col-12">
                    <p className="mb-0">
                      Already have an account? <a href="/">Log in</a>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};


export default Register
