import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Myaccount = () => {

    const [Username, setUsername] = useState(sessionStorage.getItem("Username"));
    const [email, setemail] = useState(sessionStorage.getItem("email"));
    const [userId, setUserId] = useState(sessionStorage.getItem("userId"));

    const [bookingData, setBookingData] = useState([]);

    useEffect(() => {
        // Fetch user's booking history when the component mounts
        const fetchBookingData = async () => {
            try {
                // Make a request to your backend API to get the user's booking data
                const response = await axios.get('http://localhost:3000/api/user/bookings', {
                    // Include any necessary headers or authentication tokens
                });

                setBookingData(response.data);
            } catch (error) {
                console.error('Error fetching booking data:', error);
            }
        };

        fetchBookingData();
    }, []); // Empty dependency array ensures the effect runs only once on mount


    

    return (
        <div>
            <div className="container">
                <div className="page">
                    <div className="breadcrumbs">
                        <a href="/">Home</a>
                        <span>My Account</span>
                    </div>

                    <div className="content">
                        <div className="row">
                            <div className="col-md-4">
                                <h2>My Account</h2>
                                <div className="contact-form">
                                    <div className="col-12">
                                    <h4>Welcome, {Username}!</h4>
                                        <p>Email: {email}</p>
                                        <h3>Booking History</h3>
                                        <table>
                                            <thead>
                                                <tr>
                                                    <th>Movie</th>
                                                    <th>Booked Seats</th>
                                                    <th>Ticket Count</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {bookingData.map((booking) => (
                                                    <tr key={booking._id}>
                                                        <td>{booking.movie_id}</td>
                                                        <td>{booking.booked_seats.join(', ')}</td>
                                                        <td>{booking.ticket_count}</td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                        <button >Log Out</button>
                                    </div>
                                   

                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </div>



        </div>
    )
}

export default Myaccount
