import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Modal from 'react-modal';
import './styles/Seat.css';

const MovieBooking = () => {

    const { movieId } = useParams();
    const [movieData, setMovieData] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);


    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:3000/api/getmovie/${movieId}`);
                console.log(response.data);
                setMovieData(response.data);
            } catch (error) {
                console.error('Error fetching movie data:', error);
            }
        };

        fetchData();
    }, [movieId]);

    if (!movieData) {
        return <div>Loading...</div>; // Add a loading state
    }

    const {
        movie_name,
        img_url,  // Adjust this according to your actual property name
        category,
        languages,
        no_seats,
        cast,
        description,
        ticket_rate,


    } = movieData;

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const handleBookTicket = async () => {
        // Implement the logic to book a ticket (e.g., make a POST request to your backend)
        // After successful booking, you can close the modal and show a confirmation message.
        closeModal();
        // You can also trigger an email confirmation here.
    };

    const GenerateSeats = (seatNumbers) => {
        return (
            <div className="row">
                {
                    seatNumbers.map((seatNumber) => {
                        return <Seat seatno={seatNumber} key={seatNumber}/>
                    })
                }
            </div>
        )
    }


    return (
        <div>

            <div className="container">
                <div className="page">
                    <div className="breadcrumbs">
                        <a href="/movies">Home</a>
                        <span>Movie Booking </span>
                    </div>

                    <div className="content">

                        <div className="row">
                            <div className="col-md-6">
                                <figure className="movie-poster">
                                    <img src={img_url} alt="#" />
                                </figure>
                            </div>
                            <div className="col-md-6">
                                <h2 className="movie-title">{movie_name}</h2>
                                <div className="movie-summary">
                                    <p>
                                        {description}
                                    </p>
                                </div>
                                <ul className="movie-meta">
                                    <li>
                                        <strong>Rating:</strong>
                                        <div className="star-rating" title="Rated 4.00 out of 5">
                                            <span style={{ width: '80%' }}>
                                                <strong className="rating">4.00</strong> out of 5
                                            </span>
                                        </div>
                                    </li>
                                    <li><strong>Length:</strong> 98 min</li>
                                    <li><strong>Category:</strong>{category}</li>
                                    <li><strong>Languages:</strong>{languages}</li>
                                </ul>

                                <ul className="starring">
                                    <li><strong>Casting:</strong>{cast}</li>
                                    {/* <li><strong>Writers:</strong> </li>
                                    <li><strong>Stars:</strong></li> */}
                                </ul>
                                <button className='button' onClick={openModal}>Book Tickets</button>
                                <Modal
                            isOpen={isModalOpen}
                            onRequestClose={closeModal}
                            contentLabel="Book Tickets"
                            // style={{
                            //     content: {
                            //         // Add your custom styles for the modal content here
                            //         // For example, you can set width, padding, etc.
                            //         width: '400px',
                            //         padding: '20px',
                            //         margin: 'auto',
                            //     },
                            // }}
                        >
                            <h2>Book Tickets</h2>
                            {/* Display ticket availability options (Available, Fast Filling, Housefull) */}
                            <p>Ticket Availability: Available</p>

                                <ul className="movie-meta">

                                    <li><strong>Timing :</strong> 98 min</li>
                                    <li><strong>Total Seats :</strong>  {no_seats}</li>
                                    <li><strong>Available No Of Seats :</strong>  {no_seats}</li>
                                    <li><strong>Languages:</strong>{languages}</li>
                                </ul>

                                <div className="movie-complex">
			<p>Screen This way!</p>
			<div className="container row movie-layout">
				<div className="movie-column-1">
					{GenerateSeats([1,2,3,4])}
					{GenerateSeats([5,6,7,8])}
				</div>
				<div className="movie-column-2">
					{GenerateSeats([13, 14, 15, 16, 17])}
					{GenerateSeats([18, 19, 20, 21, 22])}
					{GenerateSeats([23, 24, 25, 26, 27])}
					{GenerateSeats([28, 29, 30, 31, 32])}
				</div>
				<div className="movie-column-3">
					{GenerateSeats([33,34,35,36])}
					{GenerateSeats([37,38,39,40])}
				</div>
			</div>
		</div>
                            {/* Add a form or any additional UI for the booking process */}
                            {/* For simplicity, just a button to simulate booking in this example */}
                            <button className='button' onClick={handleBookTicket}>Book Ticket</button>

                            <div style={{ marginTop: '10px' }}>
                                <button className='button' onClick={closeModal}>Close</button>
                            </div>
                        </Modal>
                            </div>
                        </div> {/* .row */}
                        <div className="entry-content">
                            {/* ... Content paragraphs go here ... */}
                        </div>
                        {/* Ticket Booking Modal */}
                      
                    </div>
                </div>
            </div>

        </div>
    )
}

export default MovieBooking
