import React,{ useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

const Addemployee = (props) => {
    const navigate=useNavigate();
  const[userToken,setUserToken] =useState(sessionStorage.getItem("userToken"));


  const[post,setPost] = useState(props.data);
  console.log("method",props.method);
  console.log("data",props.data);
  const inputHandler=(e)=>{
    const {name,value} = e.target;
    setPost({
      ...post,[name]:value
    })
    console.log(post);
  }
  const addPost=()=>{
    let data={
    //   userId:userID,
        token:userToken,
        name:post.name,
        address:post.address,
        phone:post.phone,
        email:post.email,
        post:post.post,
        salary:post.salary,
    }
    console.log("addemployee clicked",post);
    if(props.method==="post")
    {

        axios.post("http://localhost:3000/api/postdata",data)
        .then((response)=>{
          if(response.data.message==="employee added successfully")
          {
            alert(response.data.message);
            navigate('/viewemployee')
          }
          else
          {
            alert(response.data.message);
          }

        })
        .catch(err=>console.log(err))
    }
    if(props.method=="put")
    {
      axios.put("http://localhost:3000/api/edit/"+post._id,post)
        .then((response)=>{
          if(response.data.message==="Updated Successfully")
          {
            alert(response.data.message);
            window.location.reload(false);
          }
          else
          {
            alert(response.data.message);
          }

        })
        .catch(err=>console.log(err))
    }
}

  return (
    <div>

<div className="container">
        <section className="section min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
          <div className="container">
            <div className="row justify-content-center">
              <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
                <div className="d-flex justify-content-center">
                  <a href="index.html" className="logo d-flex align-items-center w-auto">
                    <img src="./assets/img/ict_logo.png" alt="" />
                    <span className="d-none d-lg-block">ICTAK Curriculum Tracker</span>
                  </a>
                </div>

                <div className="card">
                  <div className="card-body">
                    <div className="pb-2">
                      <h5 className="card-title text-center fs-4">Signup</h5>
                    </div>

                    <div className="col-12">
                      <label htmlFor="yourName" className="form-label">
                        Name
                      </label>
                      <input
                        type="text"
                        name="name"
                        className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                        onChange={inputHandler}
                        required
                      />
                      {errors.name && <div className="invalid-feedback d-block">{errors.name}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="yourEmail" className="form-label">
                        Email
                      </label>
                      <input
                        type="email"
                        name="email"
                        className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                        onChange={inputHandler}
                        required
                      />
                      {errors.email && <div className="invalid-feedback d-block">{errors.email}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="yourUsername" className="form-label">
                        Username
                      </label>
                      <div className="input-group has-validation">
                        <span className="input-group-text" id="inputGroupPrepend">
                          @
                        </span>
                        <input
                          type="text"
                          name="username"
                          className={`form-control ${errors.username ? 'is-invalid' : ''}`}
                          id="yourUsername"
                          onChange={inputHandler}
                          required
                        />
                        {errors.username && <div className="invalid-feedback d-block">{errors.username}</div>}
                      </div>
                    </div>
                    <div className="col-12">
                      <label htmlFor="yourPassword" className="form-label">
                        Password
                      </label>
                      <input
                        type="password"
                        name="password"
                        className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                        id="yourPassword"
                        onChange={inputHandler}
                        required
                      />
                      {errors.password && <div className="invalid-feedback d-block">{errors.password}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="confirmPassword" className="form-label">
                        Confirm Password
                      </label>
                      <input
                        type="password"
                        name="confirmPassword"
                        className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                        id="confirmPassword"
                        onChange={inputHandler}
                        required
                      />
                      {errors.confirmPassword && (
                        <div className="invalid-feedback d-block">{errors.confirmPassword}</div>
                      )}
                    </div>

                    <div className="col-12">
                      <button className="btn btn-primary btn-block" onClick={submitHandler}>
                        Create Account
                      </button>
                    </div>
                    <div className="col-12">
                      <p className="mb-0">
                        Already have an account? <a href="/">Log in</a>
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>



        <div className="container">
        <div className="row">
          <div className="col col-12 col-sm-12 col-md-12">
            <div className="row g-3">
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Name</label>
                <input type="text" className="form-control" name="name" value={post.name} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-nd-12">
                <label htmlFor="" className="form-label" >Address</label>
                <textarea name="address" cols="15" rows="10" className="form-control" placeholder='Type a Address' value={post.address} onChange={inputHandler} ></textarea>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Phone Number</label>
                <input type="text" className="form-control" name="phone" value={post.phone} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Email</label>
                <input type="text" className="form-control" name="email" value={post.email} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Post</label>
                <input type="text" className="form-control" name="post" value={post.post} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Salary</label>
                <input type="text" className="form-control" name="salary" value={post.salary} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-nd-12">
                <button className="btn btn-primary" onClick={addPost}>Add Employee</button>
              </div>
            </div>
          </div>
        </div>
      </div>

    </div>
  )
}

export default Addemployee
