import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Modal from 'react-modal';
import './styles/Seat.css';

const MovieBooking = () => {
  const { movieId } = useParams();
  const [movieData, setMovieData] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedSeats, setSelectedSeats] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/getmovie/${movieId}`);
        console.log(response.data);
        setMovieData(response.data);
      } catch (error) {
        console.error('Error fetching movie data:', error);
      }
    };

    fetchData();
  }, [movieId]);

  const {
    movie_name,
    img_url,  // Adjust this according to your actual property name
    category,
    languages,
    no_seats,
    available_no_seats,

    cast,
    description,
    ticket_rate,


} = movieData;

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedSeats([]); // Reset selected seats when closing the modal
  };

  const handleSeatClick = (seatNumber) => {
    // Toggle the selected state of the seat
    const isSelected = selectedSeats.includes(seatNumber);
    const updatedSeats = isSelected
      ? selectedSeats.filter((seat) => seat !== seatNumber)
      : [...selectedSeats, seatNumber];

    // Update the selected seats state
    setSelectedSeats(updatedSeats);
  };

  const handleBookTicket = async () => {
    // Implement logic to book tickets and update collections
    try {
      // Make a POST request to your backend to book tickets
      const response = await axios.post('http://localhost:3000/api/booktickets', {
        movieId,
        seatNumbers: selectedSeats,
      });

      // Display success message or handle further actions
      console.log('Booking successful:', response.data);

      // After successful booking, you can close the modal and show a confirmation message.
      closeModal();
    } catch (error) {
      console.error('Error booking tickets:', error);
    }
  };

  if (!movieData) {
    return <div>Loading...</div>;
  }

//   const { no_seats } = movieData;

  return (
    <div>

    <div className="container">
        <div className="page">
            <div className="breadcrumbs">
                <a href="/movies">Home</a>
                <span>Movie Booking </span>
            </div>

            <div className="content">

                <div className="row">
                    <div className="col-md-6">
                        <figure className="movie-poster">
                            <img src={img_url} alt="#" />
                        </figure>
                    </div>
                    <div className="col-md-6">
                        <h2 className="movie-title">{movie_name}</h2>
                        <div className="movie-summary">
                            <p>
                                {description}
                            </p>
                        </div>
                        <ul className="movie-meta">
                            <li>
                                <strong>Rating:</strong>
                                <div className="star-rating" title="Rated 4.00 out of 5">
                                    <span style={{ width: '80%' }}>
                                        <strong className="rating">4.00</strong> out of 5
                                    </span>
                                </div>
                            </li>
                            <li><strong>Length:</strong> 98 min</li>
                            <li><strong>Category:</strong>{category}</li>
                            <li><strong>Languages:</strong>{languages}</li>
                        </ul>

                        <ul className="starring">
                            <li><strong>Casting:</strong>{cast}</li>
                            {/* <li><strong>Writers:</strong> </li>
                            <li><strong>Stars:</strong></li> */}
                        </ul>
                        <button className='button' onClick={openModal}>Book Tickets</button>
                        <Modal
                            isOpen={isModalOpen}
                            onRequestClose={closeModal}
                            contentLabel="Book Tickets"
                            // style={{
                            //     content: {
                            //         width: '600px', // Adjust the width as needed
                            //         margin: 'auto',
                            //     },
                            // }}
                        >
                            <h2>Book Tickets</h2>

                            <p>Ticket Availability: Available</p>

                            <ul className="movie-meta">
                                <li><strong>Timing :</strong> 98 min</li>
                                <li><strong>Total Seats :</strong> {no_seats}</li>
                                <li><strong>Available No Of Seats :</strong> {no_seats}</li>
                            </ul>

                            {/* Seat Booking Matrix */}
                            <div className="seat-matrix">
                                <h3>Select Your Seats</h3>
                                <div className="matrix-container">
                                    {/* Create a grid of seats based on the number of available seats */}
                                    {Array.from({ length: no_seats }, (_, index) => (
                                        <div
                                            key={index}
                                            className={`seat ${selectedSeats.includes(index + 1) ? 'selected' : ''}`}
                                            onClick={() => handleSeatClick(index + 1)}
                                        >
                                            {index + 1}
                                        </div>
                                    ))}
                                </div>
                            </div>

                             {/* Display the selected seats as a comma-separated string */}
                            <p>Selected Seats: {selectedSeats.join(', ')}</p>
                            <p>Selected Seats Count: {selectedSeats.length}</p>
                            {/* For simplicity, just a button to simulate booking in this example */}
                            <button className='button' onClick={handleBookTicket}>Book Ticket</button>

                            <div style={{ marginTop: '10px' }}>
                                <button className='button' onClick={closeModal}>Close</button>
                            </div>
                        </Modal>
                    </div>
                </div> {/* .row */}
                <div className="entry-content">
                    {/* ... Content paragraphs go here ... */}
                </div>
                {/* Ticket Booking Modal */}

            </div>
        </div>
    </div>

</div>
  );
};

export default MovieBooking;
