import React, { useState } from 'react'
import Header from './Header'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

const Addpost = (props) => {
  const navigate=useNavigate();
  const[post,setPost] = useState(props.data);
  console.log("method",props.method);
  console.log("data",props.data);
  const inputHandler=(e)=>{
    const {name,value} = e.target;
    setPost({
      ...post,[name]:value
    })
    console.log(post);
  }
  const addPost=()=>{
    console.log("addPost clicked",post);
    if(props.method==="post")
    {

        axios.post("http://localhost:3000/api/postbdata",post)
        .then((response)=>{
          if(response.data.message==="Post added sucecessfully")
          {
            alert(response.data.message);
            navigate('/viewpost')
          }
          else
          {
            alert(response.data.message);
          }

        })
        .catch(err=>console.log(err))
    }
    if(props.method=="put")
    {
      axios.put("http://localhost:3000/api/edit",post._id,post)
        .then((response)=>{
          if(response.data.message==="Updated sucecessfully")
          {
            alert(response.data.message);
            navigate('/viewpost')
          }
          else
          {
            alert(response.data.message);
          }

        })
        .catch(err=>console.log(err))
    }

  }
  return (
    <div>
    
      <div className="container">
        <div className="row">
          <div className="col col-12 col-sm-12 col-md-12">
            <div className="row g-3">
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Title</label>
                <input type="text" className="form-control" name="title" value={post.title} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-nd-12">

                <textarea name="description" cols="30" rows="10" className="form-control" placeholder='Type a post' value={post.description} onChange={inputHandler} ></textarea>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >image URL</label>
                <input type="text" className="form-control" name="urlToImage" placeholder="image URL" value={post.urlToImage} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-nd-12">
                <button className="btn btn-primary" onClick={addPost}>Addpost</button>
              </div>
            </div>
          </div>
        </div>
      </div>

    </div>
  )
}

export default Addpost
