import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const MovieBooking = () => {

    const { movieId } = useParams();
    const [movieData, setMovieData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:3000/api/getmovie/${movieId}`);
                console.log(response.data);
                setMovieData(response.data);
            } catch (error) {
                console.error('Error fetching movie data:', error);
            }
        };

        fetchData();
    }, [movieId]);

    if (!movieData) {
        return <div>Loading...</div>; // Add a loading state
    }


    return (
        <div>

            <div className="container">
                <div className="page">
                    <div className="breadcrumbs">
                        <a href="/movies">Home</a>
                        <a href="#">Movie Booking</a>
                        <span>s</span>
                    </div>

                    <div className="content">
                        {data.map((value, index) => {
                            return <div className="movie" key={index}>
                                <figure className="movie-poster"><img src={value.img_url} alt={value.movie_name} style={{ width: '220px', height: '300px' }} /></figure>
                                <div className="movie-title">
                                    {/* <a href="/booking">{value.movie_name}</a> */}
                                    <Link to={`/booking/${value._id}`}>{value.movie_name}</Link>
                                </div>
                                <p><strong>Category:</strong> {value.category}</p>
                                <p><strong>Languages:</strong> {value.languages}</p>
                                <strong>Rating:</strong>
                                <div className="star-rating" title="Rated 4.00 out of 5">
                                    <span style={{ width: '80%' }}>
                                        <strong className="rating">4.00</strong> out of 5
                                    </span>
                                </div>
                                <p>{value.description}</p>
                            </div>
                        })}
                        
                    </div>
                </div>
            </div>

        </div>
    )
}

export default MovieBooking
