import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState({});
    const [showPassword, setShowPassword] = useState(false);
    const [errors, setErrors] = useState({});

    const inputHandler = (e) => {

        if (Object.keys(errors).length > 0) {
            validateFields();
        }

        setUser({
            ...user, [e.target.name]: e.target.value
        })
        console.log(user);
    }
    const addHandler = () => {
        console.log("button clicked", user);
        if (validateFields()) {

            axios.post("http://localhost:3000/api/login", user)
                .then((response) => {
                    console.log(response);
                    console.log(response.data.message);
                    if (response.data.message === "Login sucessfully") {
                        const token = response.data.token;
                        const userid = response.data.data._id;
                        const userStatus = response.data.data.userStatus;
                        console.log(response);
                        console.log(token);
                        console.log(userid);
                        console.log(userStatus);
                        sessionStorage.setItem("userToken", token);
                        sessionStorage.setItem("userId", userid);
                        sessionStorage.setItem("userStatus", userStatus);
                        alert(response.data.message);
                        navigate('/movies')

                    }
                    else {
                        alert("Invalid username or password");
                        window.location.reload(false);

                    }
                })
        }
    }
    const toggleShowPassword = () => {
        setShowPassword(!showPassword);
    };

    const validateFields = () => {
        const { username, password } = user;
        const newErrors = {};
        if (!username) {
            newErrors.username = 'Please enter your username!';
        }
        if (!password) {
            newErrors.password = 'Please enter your password!';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };


    return (
        <div>
            <div id="site-content">
                <header className="site-header">
                    <div className="container">
                        <a href="index.html" id="branding">
                            <img src="images/logo.png" alt="" className="logo" />
                            <div className="logo-copy">
                                <h1 className="site-title">VB Movies</h1>
                                <small className="site-description">Where Stories Come to Life</small>
                            </div>
                        </a>

                        <div className="main-navigation">
                            <button type="button" className="menu-toggle"><i className="fa fa-bars"></i></button>
                            <ul className="menu">
                                <li className="menu-item current-menu-item"><a href="/">Home</a></li>
                                <li className="menu-item"><a href="about.html">About</a></li>
                                <li className="menu-item"><a href="/review">Movies</a></li>
                                <li className="menu-item"><a href="/login">Login</a></li>
                                <li className="menu-item"><a href="#">SignUp</a></li>
                            </ul>

                            <form action="#" className="search-form">
                                <input type="text" placeholder="Search..." />
                                <button><i className="fa fa-search"></i></button>
                            </form>
                        </div>

                        <div className="mobile-navigation"></div>
                    </div>
                </header>

                <main className="main-content">
                    <div className="container">
                        <div className="page">
                            <div className="breadcrumbs">
                                <a href="">Home</a>
                                <span>Login</span>
                            </div>

                            <div className="content">
                                <div className="row">
                                    <div className="col-md-4">
                                        <h2>Contact</h2>
                                        <ul className="contact-detail">
                                            <li>
                                                <img src="images/icon-contact-map.png" alt="#" />
                                                <address>
                                                    <span>Company name. INC</span> <br />
                                                    550 Avenue Street, New york
                                                </address>
                                            </li>
                                            <li>
                                                <img src="images/icon-contact-phone.png" alt="" />
                                                <a href="tel:1590912831">+1 590 912 831</a>
                                            </li>
                                            <li>
                                                <img src="images/icon-contact-message.png" alt="" />
                                                <a href="mailto:contact@companyname.com">contact@companyname.com</a>
                                            </li>
                                        </ul>
                                        <div className="contact-form">
                                            <input type="text" className="name" placeholder="name..." />
                                            <input type="text" className="email" placeholder="email..." />
                                            <input type="text" className="website" placeholder="website..." />
                                            <textarea className="message" placeholder="message..."></textarea>
                                            <input type="submit" value="Send Message " />
                                        </div>
                                    </div>
                                    <div className="col-md-7 col-md-offset-1">
                                        <div className="map"></div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </main>

                <footer className="site-footer">
          <div className="container">
            {/* Rest of the footer content */}
          </div>
        </footer>
      </div>


