import React,{ useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

const Addemployee = (props) => {
    const navigate=useNavigate();
    const[userToken,setUserToken] =useState(sessionStorage.getItem("userToken"));
    
  
    const[post,setPost] = useState(props.data);

  console.log("method",props.method);
  console.log("data",props.data);

  const inputHandler=(e)=>{

    if (Object.keys(errors).length > 0) {
      validateFields();
    }

    const {name,value} = e.target;
    setPost({
      ...post,[name]:value
    })
    console.log(post);
  }


  const addPost=()=>{
    if (validateFields()) {
    let data={
   
        token:userToken,
        name:post.name,
        address:post.address,
        phone:post.phone,
        email:post.email,
        post:post.post,
        salary:post.salary,
    }
    console.log("addemployee clicked",post);
    if(props.method==="post")
    {
        axios.post("http://localhost:3000/api/postdata",data)
        .then((response)=>{
          if(response.data.message==="employee added successfully")
          {
            alert(response.data.message);
            navigate('/viewemployee')
          }
          else
          {
            alert(response.data.message);
          }

        })
        .catch(err=>console.log(err))
    }
    if(props.method=="put")
    {
      axios.put("http://localhost:3000/api/edit/"+post._id,post)
        .then((response)=>{
          if(response.data.message==="Updated Successfully")
          {
            alert(response.data.message);
            window.location.reload(false);
          }
          else
          {
            alert(response.data.message);
          }

        })
        .catch(err=>console.log(err))
    }
  }
}

const validateFields = () => {
  const { name, email,phone,post,salary} = inputs;
  const newErrors = {};

  if (!name) {
    newErrors.name = 'Please enter your name!';
  }
  if (!email) {
    newErrors.email = 'Please enter your email!';
  } else if (!/\S+@\S+\.\S+/.test(email)) {
    newErrors.email = 'Please enter a valid email address!';
  }
  if (!phone) {
    newErrors.phone = 'Please enter your phone!';
  }else if (phone.length !== 10 || isNaN(phone)) {
    newErrors.phone = 'Phone number must be 10 digits long and contain only numbers!';
  }
  if (!post) {
    newErrors.post = 'Please enter your degignation!';
  }
  if (!salary) {
    newErrors.salary = 'Please enter your salary!';
  }else if (isNaN(salary)) {
    newErrors.salary = 'Salary must be a number!';
  }
 
  setErrors(newErrors);
  return Object.keys(newErrors).length === 0;
};



  return (
    <div>

<div className="container">
        <section className="section min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
          <div className="container">
            <div className="row justify-content-center">
              <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
                <br/>
                <div className="d-flex justify-content-center">
                  <h1>Employee Registration</h1>
                </div>
                 <br/>
                <div className="card">
                  <div className="card-body">
                    <br/>
                    <div className="col-12">
                      <label htmlFor="" className="form-label" >Name</label>
                      <input type="text" className="form-control" name="name" value={post.name} onChange={inputHandler}/>
                      {errors.name && <div className="invalid-feedback d-block">{errors.name}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="" className="form-label" >Address</label>
                      <textarea name="address" cols="10" rows="5" className="form-control" placeholder='Type a Address' value={post.address} onChange={inputHandler} ></textarea>
                      
                    </div>
                    <div className="col-12">
                        <label htmlFor="" className="form-label" >Phone Number</label>
                        <input type="text" className="form-control" name="phone" value={post.phone} onChange={inputHandler}/>
                        {errors.phone && <div className="invalid-feedback d-block">{errors.phone}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="" className="form-label" >Email</label>
                      <input type="text" className="form-control" name="email" value={post.email} onChange={inputHandler}/>
                      {errors.email && <div className="invalid-feedback d-block">{errors.email}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="" className="form-label" >Designation</label>
                      <input type="text" className="form-control" name="post" value={post.post} onChange={inputHandler}/>
                      {errors.post && <div className="invalid-feedback d-block">{errors.post}</div>}
                    </div>
                    <div className="col-12">
                      <label htmlFor="" className="form-label" >Salary</label>
                      <input type="text" className="form-control" name="salary" value={post.salary} onChange={inputHandler}/>
                      {errors.salary && <div className="invalid-feedback d-block">{errors.salary}</div>}
                    </div>
                    <br />
                    <div className="col-12">
                        <button className="btn btn-success" onClick={addPost}>Add Employee</button>
                    </div>
                    
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>



        <div className="container">
        <div className="row">
          <div className="col col-12 col-sm-12 col-md-12">
            <div className="row g-3">
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Name</label>
                <input type="text" className="form-control" name="name" value={post.name} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-nd-12">
                <label htmlFor="" className="form-label" >Address</label>
                <textarea name="address" cols="15" rows="10" className="form-control" placeholder='Type a Address' value={post.address} onChange={inputHandler} ></textarea>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Phone Number</label>
                <input type="text" className="form-control" name="phone" value={post.phone} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Email</label>
                <input type="text" className="form-control" name="email" value={post.email} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Designation</label>
                <input type="text" className="form-control" name="post" value={post.post} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-md-12 col-lg-12">
                <label htmlFor="" className="form-label" >Salary</label>
                <input type="text" className="form-control" name="salary" value={post.salary} onChange={inputHandler}/>
              </div>
              <div className="col col-12 col-sm-12 col-nd-12">
                <button className="btn btn-primary" onClick={addPost}>Add Employee</button>
              </div>
            </div>
          </div>
        </div>
      </div>

    </div>
  )
}

export default Addemployee
